import React from "react";

import "./productInfo.scss";

import { useSelector } from "react-redux";
import { useParams } from "react-router";

const ProductInfo = () => {
  const { pid } = useParams();

  const productList = useSelector((state) => state.product.prodcutItems);

  const productById = productList.find((prod) => prod._id === pid);

  console.log(productById);

  return (
    <section className="product-info">
      <div className="container">
        <div className="product-info__content">
          <div className="product-info__left">
            <div className="product-info__image">
              <img
                src="https://sm.ign.com/ign_in/screenshot/default/chainsaw-man_fkbm.jpg"
                alt=""
              />
            </div>
            <p className="product-info__title"></p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default ProductInfo;
