import React from "react";

import "./productInfo.scss";

const ProductInfo = ({ product }) => {
  console.log(product);
  return (
    <section className="product-info">
      <div className="container">
        <div className="product-info__content">
          <div className="product-info__left">
            <div className="product-info__image">
              <img
                src="https://sm.ign.com/ign_in/screenshot/default/chainsaw-man_fkbm.jpg"
                alt=""
              />
            </div>
          </div>
          <div className="product-info__specs">
            <p className="product-info__title">{product.title}</p>
            <p className="product-info__brand">{product.brand}</p>
            <div className="product-info__rating">Rating: {product.rating}</div>
          </div>
          <div className="product-info__price-quantity">
            <p className="product-info__price">Rs.{product.price}</p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default ProductInfo;
